import React from 'react';
import InputBase from '@material-ui/core/InputBase';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';

const containerStyle = {
  maxHeight: 200,
  overflow: 'auto',
}

const listItemStyle = {
  width: 250,
  justifyContent: 'space-between',
  borderBottomWidth: 1,
  borderBottomStyle: 'solid',
  borderBottomColor: '#e0e0e0',
}

const firstListItemStyle = {
  marginTop: -8,
  width: 250,
  justifyContent: 'space-between',
  borderBottomWidth: 1,
  borderBottomStyle: 'solid',
  borderBottomColor: '#e0e0e0',
}

const addItemStyle = {
  width: 250,
  justifyContent: 'space-between',
  borderBottomWidth: 1,
  borderBottomStyle: 'solid',
  borderBottomColor: '#e0e0e0',
  height: 50,
}

const deleteIconStyle = {
  marginLeft: 10,
}

export default class EditableList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addItem: '',
      authors: [
        "Hui Zou",
        "Kenneth Lange",
        "Sanjay Chaudhuri",
        "Ryan Tibshirani",
        "Elizaveta Levina",
        "Jianqing Fan",
        "Ming Yuan",
        "Kshitij Khare",
        "Ji Zhu",
        "Mathias Drton",
      ],
    }
  }

  setAddItemState(event) {
    this.setState({
      addItem: event.target.value,
    })
  }

  addItem() {

  }

  render() {
    const { navigation } = this.props;
    const { authors, addItem } = this.state;

    return (
      <div style={containerStyle}>
        <ListItem style={addItemStyle}>
        <InputBase
          placeholder='Add Name'
          disableUnderline={true}
          value={addItem}
          onChange={event => this.setAddItemState(event)}
        />
        <AddIcon
        style={deleteIconStyle}
        onClick={() => this.addItem()}
        />
        </ListItem>
        <List>
          {authors.map((author, index) => {
            return index == 0 ?
            (<ListItem key={index} style={firstListItemStyle}>
              {author}
            <DeleteIcon style={deleteIconStyle} />
            </ListItem>)
            :
            (<ListItem key={index} style={listItemStyle}>
              {author}
            <DeleteIcon style={deleteIconStyle} />
            </ListItem>)
          })}
        </List>
      </div>
    );
  }
}
