import React from 'react';
import InputBase from '@material-ui/core/InputBase';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';

const containerStyle = {
  maxHeight: 400,
  overflow: 'auto',
  backgroundColor: 'white',
  marginLeft: 20,
  marginRight: 20,
}

const listItemStyle = {
  width: 250,
  justifyContent: 'space-between',
  borderBottomWidth: 1,
  borderBottomStyle: 'solid',
  borderBottomColor: '#e0e0e0',
  height: 50,
}

const firstListItemStyle = {
  marginTop: -8,
  width: 250,
  justifyContent: 'space-between',
  borderBottomWidth: 1,
  borderBottomStyle: 'solid',
  borderBottomColor: '#e0e0e0',
}

const addItemStyle = {
  width: 250,
  justifyContent: 'space-between',
  borderBottomWidth: 1,
  borderBottomStyle: 'solid',
  borderBottomColor: '#e0e0e0',
}

const deleteIconStyle = {
  marginLeft: 10,
}

export default class EditableList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addItem: '',
      authors: [],
    }
  }

  componentWillMount() {
    const { type } = this.props;
    if(type == 'Citing') {
      fetch("http://localhost:8080/Citation-Backend/getCitingAuthors")
        .then(res => res.json())
        .then(
          (result) => {
            const authors = result.map(item => item.name);
            this.setState({
              authors: authors,
            });
          },
          (error) => {
            console.log(error);
          }
        )
    }
    else if(type == 'Cited') {
      fetch("http://localhost:8080/Citation-Backend/getCitedAuthors")
        .then(res => res.json())
        .then(
          (result) => {
            const authors = result.map(item => item.name);
            this.setState({
              authors: authors,
            });
          },
          (error) => {
            console.log(error);
          }
        )
    }
  }

  setAddItemState(event) {
    this.setState({
      addItem: event.target.value,
    })
  }

  addItem() {
    var authors = this.state.authors;
    authors.unshift(this.state.addItem);
    this.setState({
      authors: authors,
      addItem: '',
    })
  }

  deleteItem(toDeleteAuthor) {
    var authors = this.state.authors;
    authors = authors.filter(author => author !== toDeleteAuthor);
    this.setState({
      authors: authors,
    })
  }

  renderList() {
    const { authors } = this.state;
    return authors && authors.length > 0 ? (
      <List>
        {authors.map((author, index) => {
          return index == 0 ?
          (<ListItem key={index} style={firstListItemStyle}>
            {author}
          <DeleteIcon
          style={deleteIconStyle}
          onClick={() => this.deleteItem(author)}
           />
          </ListItem>)
          :
          (<ListItem key={index} style={listItemStyle}>
            {author}
          <DeleteIcon
          style={deleteIconStyle}
          onClick={() => this.deleteItem(author)}
           />
          </ListItem>)
        })}
      </List>
    ) :
    null;
  }

  render() {
    const { navigation } = this.props;
    const { authors, addItem } = this.state;

    return (
      <div style={containerStyle}>
        <ListItem style={addItemStyle}>
        <InputBase
          placeholder='Add Name'
          disableUnderline={true}
          value={addItem}
          onChange={event => this.setAddItemState(event)}
        />
        <AddIcon
        style={deleteIconStyle}
        onClick={() => this.addItem()}
        />
        </ListItem>
        {this.renderList()}
      </div>
    );
  }
}
