import React from 'react';
import step1 from './cstep1.png';
import step2 from './cstep2.png';

const containerStyle = {
  display: 'flex',
  flexDirection: 'column',
};

const headerStyle = {
  marginTop: 50,
  fontSize: 30,
  marginBottom: 50,
  textAlign: 'center',
};

const paragraphStyle = {
  fontSize: 15,
  marginLeft: 20,
}

const stepHeaderStyle = {
  fontSize: 30,
  fontWeight: 'bold',
  marginLeft: 20,
  marginTop: 20,
  marginBottom: 20,
}

export default class CalendarInstructions extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {

    return (
    <div style={containerStyle}>
      <div style={headerStyle}>
        Import events to iCal
      </div>
      <div style={paragraphStyle}>
        You can transfer your events from a different calendar application to iCal.
      </div>

      <div style={stepHeaderStyle}>
        Step 1: Open iCal
      </div>

      <div style={stepHeaderStyle}>
        Step 2: Add other calendars
      </div>
      <div style={paragraphStyle}>
        Click on the '+' next other 'Other Calendar' on the left sidebar
      </div>
      <img style={stepOneImageStyle} src={step1} />

      <div style={stepHeaderStyle}>
        Step 3: From URL
      </div>
      <div style={paragraphStyle}>
        Click on the 'From URL' option from the pop-up
      </div>
      <img style={stepTwoImageStyle} src={step2} />
    </div>
    );
  }
}
