import React from 'react';
import InputBase from '@material-ui/core/Input';
import SearchIcon from '@material-ui/icons/Search';
import EditableList from './EditableList';

const containerStyle = {
  display: 'flex',
  flexDirection: 'column',
  height: '100vh',
  backgroundImage: "url('https://api.regonline.com/CustImages/290000/299570/104475878-DENVER_1.1910x1000_copy_2.jpg')",
  backgroundSize: 'cover',
  fontWeight: 'semi-bold',
  alignItems: 'center',
};

const headerStyle = {
  marginTop: 50,
  fontSize: 50,
  color: '#FFFFFF',
  marginBottom: 50,
}

const searchStyle = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
  height: 50,
  width: 500,
  backgroundColor: '#FFFFFF',
};

const searchInputStyle = {
  marginLeft: 10,
  width: 450,
}

const listsStyle = {
  display: 'flex',
  flexDirection: 'row',
  marginBottom: 50,
}

const listTitleStyle = {
  marginLeft: 20,
  fontSize: 15,
  fontWeight: 'semi-bold',
  textAlign: 'center',
  marginBottom: 10,
}

const authors = [
  "Hui Zou",
  "Kenneth Lange",
  "Sanjay Chaudhuri",
  "Ryan Tibshirani",
  "Elizaveta Levina",
  "Jianqing Fan",
  "Ming Yuan",
  "Kshitij Khare",
  "Ji Zhu",
  "Mathias Drton",
];

export default class Result extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div style={containerStyle}>
        <div style={headerStyle}>
          Here are the people we think you'd like to hear
        </div>
        <div style={listsStyle}>
          <div>
            <div style={listTitleStyle}>People who cite you a lot</div>
            <EditableList />
          </div>
          <div>
            <div style={listTitleStyle}>People you cite a lot</div>
            <EditableList />
          </div>
          <div>
            <div style={listTitleStyle}>Others</div>
            <EditableList />
          </div>
        </div>
        <div style={searchStyle}>
          <InputBase
            placeholder="Your Name"
            disableUnderline={true}
            style={searchInputStyle}
          />
          <SearchIcon />
      </div>
    </div>
    );
  }
}
